import React from 'react';
import { Navbar, NavbarBrand, Col, Row } from "reactstrap";
import magasin from "../images/Magasin.png";

class CartSummary extends React.Component {
  constructor() {
    super();
    const cartItems = JSON.parse(localStorage.getItem('cartTrybe'));
    this.state = {
      cartItems,
    };
  }

  render() {
    const { cartItems } = this.state;
    return (
      <div>
        <Navbar className="navbar" color="white" expand="md" light>
          <NavbarBrand className="brand" href="/">
            <img src={magasin} alt="magasin-logo" width="200" />
          </NavbarBrand>
        </Navbar>
        <div class="jumbotron jumbotron-fluid">
        <div class="container">
            <h2 class="display-4">Resumo do carrinho </h2>

            { cartItems.map((item) => (
              <div key={ item.product.id }>
                <h5>{ item.product.title }</h5>
                <img
                  src={ item.product.thumbnail }
                  alt={ item.product.title }
                  width={ 70 }
                />
                <p>{ `R$ ${item.product.price}` }</p>
                <p>{ item.quantity }</p>
              </div>
            )) }
            <h3>
              { `R$ ${cartItems.reduce((sum, { product: { price }, quantity }) => (
                sum + price * quantity
              ), 0)}` }
                </h3>
          </div>
          </div>
      </div>
    );
  }
}

export default CartSummary;
